                    <Button 
                      onClick={() => setActiveSection("whatsapp")}
                      className="h-auto p-4 flex flex-col items-center space-y-2"
                      variant="outline"
                    >
                      <i className="fab fa-whatsapp text-2xl"></i>
                      <span>Setup WhatsApp</span>
