                                <SelectContent>
                                  <SelectItem value="general">General</SelectItem>
                                  <SelectItem value="header">Header</SelectItem>
                                  <SelectItem value="footer">Footer</SelectItem>
                                  <SelectItem value="seo">SEO</SelectItem>
                                </SelectContent>
                              </Select>
