                            window.location.href = "/login";
                          } catch (error) {
                            console.error("Logout error:", error);
                          }
                        }}
                        className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-50"
                        data-testid="button-logout"
                      >
                        Sign Out
                      </button>
                    </div>
