                    LOGIN
                  </Link>
                )}
                <button className="hover:text-black transition-colors" data-testid="button-wishlist">
                  <i className="far fa-heart text-gray-700 text-xl"></i>
                </button>
                <button 
                  className="hover:text-black transition-colors relative"
                  onClick={toggleCart}
                  data-testid="button-bag"
                >
                  <i className="fas fa-shopping-bag text-gray-700 text-xl"></i>
                  {cartItemCount > 0 && (
                    <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center font-bold">
                      {cartItemCount}
                    </span>
                  )}
                </button>
              </div>

            </div>
          </div>
        </div>

        {/* Secondary Navigation */}
        <div className="bg-gray-50 border-b border-gray-200">
          <div className="container mx-auto px-4">
            <div className="flex items-center justify-center space-x-8 py-3">
              <Link href="/products" className="text-sm font-medium text-gray-700 hover:text-black transition-colors" data-testid="link-shop-now">
                SHOP NOW
              </Link>
              <Link href="/live" className="text-sm font-medium text-gray-700 hover:text-black transition-colors" data-testid="link-live-now">
                LIVE NOW
              </Link>
              <Link href="/plus-size" className="text-sm font-medium text-gray-700 hover:text-black transition-colors" data-testid="link-plus-size">
                PLUS SIZE
              </Link>
              <Link href="/bewakoof-air" className="text-sm font-medium text-gray-700 hover:text-black transition-colors" data-testid="link-bewakoof-air">
                BWKF X GOOGLE AI
              </Link>
              <Link href="/accessories" className="text-sm font-medium text-gray-700 hover:text-black transition-colors" data-testid="link-accessories">
                ACCESSORIES
              </Link>
              <Link href="/official-merch" className="text-sm font-medium text-gray-700 hover:text-black transition-colors" data-testid="link-official-merch">
                OFFICIAL MERCH
              </Link>
              <Link href="/sneakers" className="text-sm font-medium text-gray-700 hover:text-black transition-colors" data-testid="link-sneakers">
                SNEAKERS
              </Link>
            </div>
          </div>
        </div>
      </header>

      {/* Mobile Menu */}
      <div className={`mobile-menu fixed inset-0 bg-white z-40 lg:hidden ${isMenuOpen ? 'open' : ''}`}>
        <div className="p-4">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-xl font-bold text-gray-800">Menu</h2>
            <button onClick={toggleMobileMenu} data-testid="button-close-menu">
              <i className="fas fa-times text-xl text-gray-600"></i>
            </button>
          </div>

          {/* Mobile Search */}
          <form onSubmit={handleSearch} className="mb-6">
            <div className="relative">
              <Input
                type="text"
                placeholder="Search products..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="pl-10 pr-4 py-2 w-full"
                data-testid="input-search-mobile"
              />
              <i className="fas fa-search absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400"></i>
            </div>
          </form>

          <nav className="space-y-4">
            <Link href="/" className="block text-gray-700 text-lg py-2" onClick={toggleMobileMenu} data-testid="link-home-mobile">
              Home
            </Link>
            <Link href="/products" className="block text-gray-700 text-lg py-2" onClick={toggleMobileMenu} data-testid="link-products-mobile">
              All Products
            </Link>
            {categories.map((category: any) => (
              <Link
                key={category.id}
                href={`/products?categoryId=${category.id}`}
                className="block text-gray-600 pl-4 py-2"
                onClick={toggleMobileMenu}
                data-testid={`link-category-mobile-${category.slug}`}
              >
                {category.name}
              </Link>
            ))}
            {isAuthenticated ? (
              <>
                <Link href="/profile" className="block text-gray-700 text-lg py-2" onClick={toggleMobileMenu} data-testid="link-profile-mobile">
                  Profile
                </Link>
                {user?.isAdmin && (
                  <Link href="/admin" className="block text-gray-700 text-lg py-2" onClick={toggleMobileMenu} data-testid="link-admin-mobile">
                    Admin
                  </Link>
                )}
                <button 
                  onClick={() => window.location.href = "/api/logout"}
                  className="block text-gray-700 text-lg py-2 w-full text-left"
                  data-testid="button-logout-mobile"
                >
                  Logout
                </button>
              </>
            ) : (
              <Link 
                href="/login"
                className="block text-gray-700 text-lg py-2 w-full text-left"
                onClick={toggleMobileMenu}
                data-testid="button-login-mobile"
              >
                Login
              </Link>
            )}
          </nav>
        </div>
      </div>
    </>
  );
}

client/src/components/product-card.tsx.content:
import { useState } from "react";
import { Link } from "wouter";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import QuickViewModal from "./quick-view-modal";

interface Product {
  id: string;
  name: string;
  slug: string;
  price: string;
  salePrice?: string;
  imageUrl: string;
  rating?: string;
  reviewCount?: number;
  sizes?: string[];
  isOnSale?: boolean;
  isFeatured?: boolean;
}

interface ProductCardProps {
  product: Product;
  showLogin?: () => void;
}

export default function ProductCard({ product, showLogin }: ProductCardProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [showQuickView, setShowQuickView] = useState(false);

  const addToCartMutation = useMutation({
    mutationFn: async (cartData: any) => {
      await apiRequest("POST", "/api/cart", cartData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Added to Cart",
        description: "Product has been added to your cart!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add product to cart",
        variant: "destructive",
      });
    },
  });

  const addToWishlistMutation = useMutation({
    mutationFn: async (productId: string) => {
      await apiRequest("POST", "/api/wishlist", { productId });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/wishlist"] });
      toast({
        title: "Added to Wishlist",
        description: "Product has been added to your wishlist!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add product to wishlist",
        variant: "destructive",
      });
    },
  });

  const handleAddToCart = (e: React.MouseEvent) => {
    e.preventDefault();

    if (!user) {
      if (showLogin) {
        showLogin();
      } else {
        toast({
          title: "Please Login",
          description: "You need to login to add items to cart",
          variant: "destructive",
        });
      }
      return;
    }

    addToCartMutation.mutate({
      productId: product.id,
      quantity: 1,
    });
  };

  const handleAddToWishlist = (e: React.MouseEvent) => {
    e.preventDefault();

    if (!user) {
      if (showLogin) {
        showLogin();
      } else {
        toast({
          title: "Please Login",
          description: "You need to login to add items to wishlist",
          variant: "destructive",
        });
      }
      return;
    }

    addToWishlistMutation.mutate(product.id);
  };

  const handleQuickView = (e: React.MouseEvent) => {
    e.preventDefault();
    setShowQuickView(true);
  };

  const displayPrice = product.salePrice || product.price;
  const originalPrice = product.salePrice ? product.price : null;
  const discountPercent = originalPrice 
    ? Math.round(((parseFloat(originalPrice) - parseFloat(displayPrice)) / parseFloat(originalPrice)) * 100)
    : 0;

  return (
    <>
      <div className="product-card bg-white rounded-lg overflow-hidden group cursor-pointer hover:shadow-lg transition-all duration-300" data-testid={`product-card-${product.id}`}>
        <Link href={`/products/${product.slug}`}>
          <div className="relative">
            <img 
              src={product.imageUrl || "https://images.unsplash.com/photo-1519238263530-99bdd11df2ea?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=400"} 
              alt={product.name}
              className="w-full h-72 object-cover group-hover:scale-105 transition-transform duration-300"
              data-testid={`img-product-${product.id}`}
            />

            {/* Rating Badge */}
            <div className="absolute top-3 left-3">
              <span className="bg-white px-2 py-1 rounded text-xs font-bold shadow-md">
                {product.rating || "4.5"} â˜…
              </span>
            </div>

            {/* Sale Badge */}
            {product.isOnSale && discountPercent > 0 && (
              <div className="absolute top-3 right-3">
                <span className="bg-red-500 text-white px-2 py-1 rounded text-xs font-bold" data-testid={`badge-sale-${product.id}`}>
                  {discountPercent}% OFF
                </span>
              </div>
            )}

            {/* Wishlist Button */}
            <div className="absolute top-3 right-3 opacity-0 group-hover:opacity-100 transition-opacity">
              <Button
                size="sm"
                variant="secondary"
                className="p-2 bg-white shadow-md hover:shadow-lg rounded-full"
                onClick={handleAddToWishlist}
                disabled={addToWishlistMutation.isPending}
                data-testid={`button-wishlist-${product.id}`}
              >
                <i className="fas fa-heart text-gray-600 hover:text-red-500 transition-colors"></i>
              </Button>
            </div>
          </div>
        </Link>

        <div className="p-4">
          <Link href={`/products/${product.slug}`}>
            <h3 className="font-medium text-gray-800 mb-1 text-sm line-clamp-2 hover:text-primary transition-colors" data-testid={`text-product-name-${product.id}`}>
              {product.name}
            </h3>
          </Link>

          {/* Brand or Category */}
          <p className="text-gray-500 text-xs mb-2">BewakoofÂ®</p>

          {/* Price */}
          <div className="flex items-center space-x-2 mb-3">
            <span className="text-lg font-bold text-gray-900" data-testid={`text-price-${product.id}`}>
              â‚¹{displayPrice}
            </span>
            {originalPrice && (
              <>
                <span className="text-gray-500 line-through text-sm" data-testid={`text-original-price-${product.id}`}>
                  â‚¹{originalPrice}
                </span>
                <span className="text-green-600 text-sm font-medium">
                  {discountPercent}% OFF
                </span>
              </>
            )}
          </div>

          {/* Size Info */}
          {product.sizes && product.sizes.length > 0 && (
            <div className="mb-3">
              <p className="text-xs text-gray-500" data-testid={`text-sizes-${product.id}`}>
                {product.sizes.slice(0, 3).join(", ")}...
              </p>
            </div>
          )}

          {/* Add to Cart Button */}
          <Button
            onClick={handleAddToCart}
            disabled={addToCartMutation.isPending}
            className="w-full bg-black text-white py-2 rounded text-sm font-medium hover:bg-gray-800 transition-colors"
            data-testid={`button-add-to-cart-${product.id}`}
          >
            {addToCartMutation.isPending ? "ADDING..." : "ADD TO CART"}
          </Button>
        </div>
      </div>

      {/* Quick View Modal */}
      {showQuickView && (
        <QuickViewModal
          product={product}
          isOpen={showQuickView}
          onClose={() => setShowQuickView(false)}
        />
      )}
    </>
  );
}

client/src/components/quick-view-modal.t.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

interface Product {
  id: string;
  name: string;
  slug: string;
  price: string;
  salePrice?: string;
  imageUrl: string;
  images?: string[];
  rating?: string;
  reviewCount?: number;
  sizes?: string[];
  colors?: string[];
  description?: string;
  shortDescription?: string;
  isOnSale?: boolean;
}

interface QuickViewModalProps {
  product: Product;
  isOpen: boolean;
  onClose: () => void;
}

export default function QuickViewModal({ product, isOpen, onClose }: QuickViewModalProps) {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [selectedSize, setSelectedSize] = useState("");
  const [selectedColor, setSelectedColor] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [activeImage, setActiveImage] = useState(0);

  const addToCartMutation = useMutation({
    mutationFn: async (cartData: any) => {
      await apiRequest("POST", "/api/cart", cartData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Added to Cart",
        description: "Product has been added to your cart!",
      });
      onClose();
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add product to cart",
        variant: "destructive",
      });
    },
  });

  const addToWishlistMutation = useMutation({
    mutationFn: async (productId: string) => {
      await apiRequest("POST", "/api/wishlist", { productId });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/wishlist"] });
      toast({
        title: "Added to Wishlist",
        description: "Product has been added to your wishlist!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add product to wishlist",
        variant: "destructive",
      });
    },
  });

  const handleAddToCart = () => {
    if (!user) {
      toast({
        title: "Please Login",
        description: "You need to login to add items to cart",
        variant: "destructive",
      });
      return;
    }

    if (product.sizes && product.sizes.length > 0 && !selectedSize) {
      toast({
        title: "Select Size",
        description: "Please select a size",
        variant: "destructive",
      });
      return;
    }

    addToCartMutation.mutate({
      productId: product.id,
      quantity,
      size: selectedSize || null,
      color: selectedColor || null,
    });
  };

  const handleAddToWishlist = () => {
    if (!user) {
      toast({
        title: "Please Login",
        description: "You need to login to add items to wishlist",
        variant: "destructive",
      });
      return;
    }

    addToWishlistMutation.mutate(product.id);
  };

  const displayPrice = product.salePrice || product.price;
  const originalPrice = product.salePrice ? product.price : null;
  const discountPercent = originalPrice 
    ? Math.round(((parseFloat(originalPrice) - parseFloat(displayPrice)) / parseFloat(originalPrice)) * 100)
    : 0;

  const images = product.images && product.images.length > 0 
    ? product.images 
    : [product.imageUrl || "https://images.unsplash.com/photo-1519238263530-99bdd11df2ea?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&h=600"];

  if (!isOpen) return null;

  return (
    <div className="modal active items-center justify-center p-4" data-testid="quick-view-modal">
      <div className="bg-white rounded-2xl max-w-4xl w-full max-h-screen overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold" data-testid="text-quick-view-title">Quick View</h2>
            <button 
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700 text-2xl"
              data-testid="button-close-quick-view"
            >
              <i className="fas fa-times"></i>
            </button>
          </div>

          <div className="grid md:grid-cols-2 gap-8">
            {/* Product Images */}
            <div>
              <img 
                src={images[activeImage]} 
                alt={product.name}
                className="w-full h-96 object-cover rounded-xl mb-4"
                data-testid="img-quick-view-main"
              />
              <div className="grid grid-cols-4 gap-2">
                {images.map((image: string, index: number) => (
                  <img
                    key={index}
                    src={image}
                    alt={`${product.name} ${index + 1}`}
                    className={`w-full h-20 object-cover rounded-lg cursor-pointer border-2 ${
                      activeImage === index ? "border-primary" : "border-gray-200"
                    }`}
                    onClick={() => setActiveImage(index)}
                    data-testid={`img-quick-view-thumbnail-${index}`}
                  />
                ))}
              </div>
            </div>

            {/* Product Details */}
            <div>
              <h3 className="text-2xl font-bold mb-4" data-testid="text-quick-view-product-name">{product.name}</h3>

              {product.rating && parseFloat(product.rating) > 0 && (
                <div className="flex items-center mb-4">
                  <div className="star-rating mr-2">
                    {Array.from({ length: 5 }, (_, i) => (
                      <i key={i} className={`${i < Math.floor(parseFloat(product.rating || "0")) ? "fas" : "far"} fa-star`}></i>
                    ))}
                  </div>
                  <span className="text-gray-500" data-testid="text-quick-view-review-count">({product.reviewCount} reviews)</span>
                </div>
              )}

              <div className="mb-6">
                <div className="flex items-center space-x-2">
                  <span className="text-3xl font-bold text-primary" data-testid="text-quick-view-price">â‚¹{displayPrice}</span>
                  {originalPrice && (
                    <>
                      <span className="text-gray-500 line-through text-xl" data-testid="text-quick-view-original-price">â‚¹{originalPrice}</span>
                      <Badge variant="destructive" data-testid="badge-quick-view-discount">{discountPercent}% OFF</Badge>
                    </>
                  )}
                </div>
              </div>

              {product.shortDescription && (
                <p className="text-gray-600 mb-6" data-testid="text-quick-view-description">{product.shortDescription}</p>
              )}

              {/* Size Selection */}
              {product.sizes && product.sizes.length > 0 && (
                <div className="mb-6">
                  <h4 className="font-semibold mb-2">Select Size/Age:</h4>
                  <Select value={selectedSize} onValueChange={setSelectedSize}>
                    <SelectTrigger className="w-full" data-testid="select-quick-view-size">
                      <SelectValue placeholder="Choose size" />
                    </SelectTrigger>
                    <SelectContent>
                      {product.sizes.map((size: string) => (
                        <SelectItem key={size} value={size}>{size}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              )}

              {/* Color Selection */}
              {product.colors && product.colors.length > 0 && (
                <div className="mb-6">
                  <h4 className="font-semibold mb-2">Select Color:</h4>
                  <Select value={selectedColor} onValueChange={setSelectedColor}>
                    <SelectTrigger className="w-full" data-testid="select-quick-view-color">
                      <SelectValue placeholder="Choose color" />
                    </SelectTrigger>
                    <SelectContent>
                      {product.colors.map((color: string) => (
                        <SelectItem key={color} value={color}>{color}</SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              )}

              {/* Quantity */}
              <div className="mb-6">
                <h4 className="font-semibold mb-2">Quantity:</h4>
                <div className="flex items-center space-x-3">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setQuantity(Math.max(1, quantity - 1))}
                    data-testid="button-quick-view-decrease"
                  >
                    -
                  </Button>
                  <span className="text-lg font-medium" data-testid="text-quick-view-quantity">{quantity}</span>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setQuantity(quantity + 1)}
                    data-testid="button-quick-view-increase"
                  >
                    +
                  </Button>
                </div>
              </div>

              {/* Action Buttons */}
              <div className="space-y-3 mb-6">
                <Button
                  onClick={handleAddToCart}
                  disabled={addToCartMutation.isPending}
                  className="w-full bg-primary text-white py-4 rounded-full font-semibold text-lg hover:bg-primary/90"
                  data-testid="button-quick-view-add-to-cart"
                >
                  {addToCartMutation.isPending ? "Adding..." : "Add to Cart"}
                </Button>
                <Button
                  variant="outline"
                  onClick={handleAddToWishlist}
                  disabled={addToWishlistMutation.isPending}
                  className="w-full py-3 rounded-full font-medium"
                  data-testid="button-quick-view-add-to-wishlist"
                >
                  {addToWishlistMutation.isPending ? "Adding..." : "Add to Wishlist"}
                </Button>
              </div>

              {/* Product Features */}
              <div className="text-sm text-gray-600 space-y-1">
                <p data-testid="text-quick-view-shipping"><i className="fas fa-truck mr-2"></i>Free shipping on orders above â‚¹1000</p>
                <p data-testid="text-quick-view-return"><i className="fas fa-undo mr-2"></i>30-day return policy</p>
                <p data-testid="text-quick-view-quality"><i className="fas fa-shield-alt mr-2"></i>Quality guaranteed</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

