import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import Header from "@/components/header";
import Footer from "@/components/footer";
import { Button } from "@/components/ui/button";
import { useLocation } from "wouter";

export default function Cart() {
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [, setLocation] = useLocation();

  const { data: cartItems = [], isLoading } = useQuery({
    queryKey: ["/api/cart"],
    retry: false,
    enabled: !!user,
  });

  const updateCartMutation = useMutation({
    mutationFn: async ({ id, quantity }: { id: string; quantity: number }) => {
      await apiRequest("PUT", `/api/cart/${id}`, { quantity });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to update cart item",
        variant: "destructive",
      });
    },
  });

  const removeFromCartMutation = useMutation({
    mutationFn: async (id: string) => {
      await apiRequest("DELETE", `/api/cart/${id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Removed",
        description: "Item removed from cart",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to remove item from cart",
        variant: "destructive",
      });
    },
  });

  const clearCartMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("DELETE", "/api/cart");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Cart Cleared",
        description: "All items removed from cart",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to clear cart",
        variant: "destructive",
      });
    },
  });

  const handleUpdateQuantity = (id: string, quantity: number) => {
    if (quantity < 1) return;
    updateCartMutation.mutate({ id, quantity });
  };

  const handleRemoveItem = (id: string) => {
    removeFromCartMutation.mutate(id);
  };

  const handleClearCart = () => {
    clearCartMutation.mutate();
  };

  const handleCheckout = () => {
    setLocation("/checkout");
  };

  const subtotal = cartItems.reduce((total: number, item: any) => {
    return total + (parseFloat(item.product?.salePrice || item.product?.price || "0") * item.quantity);
  }, 0);

  const shippingThreshold = 1000;
  const isEligibleForFreeShipping = subtotal >= shippingThreshold;
  const shippingCost = isEligibleForFreeShipping ? 0 : 100;
  const total = subtotal + shippingCost;

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8 text-center">
          <h1 className="text-2xl font-bold text-gray-800 mb-4">Please Login</h1>
          <p className="text-gray-600 mb-6">You need to login to view your cart</p>
          <Button onClick={() => window.location.href = "/api/login"}>
            Login
          </Button>
        </div>
        <Footer />
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="animate-pulse space-y-4">
            {Array.from({ length: 3 }).map((_, i) => (
              <div key={i} className="bg-white p-6 rounded-xl shadow-md">
                <div className="flex items-center space-x-4">
                  <div className="w-20 h-20 bg-gray-200 rounded-lg"></div>
                  <div className="flex-1">
                    <div className="h-4 bg-gray-200 rounded mb-2"></div>
                    <div className="h-6 bg-gray-200 rounded w-32"></div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="flex items-center justify-between mb-8">
          <h1 className="text-3xl font-bold text-gray-800" data-testid="text-cart-title">Shopping Cart</h1>
          {cartItems.length > 0 && (
            <Button 
              variant="outline" 
              onClick={handleClearCart}
              disabled={clearCartMutation.isPending}
              data-testid="button-clear-cart"
            >
              Clear Cart
            </Button>
          )}
        </div>

        {cartItems.length === 0 ? (
          <div className="text-center py-12">
            <div className="text-gray-500 text-lg mb-4" data-testid="text-empty-cart">Your cart is empty</div>
            <Button onClick={() => setLocation("/products")} data-testid="button-continue-shopping">
              Continue Shopping
            </Button>
          </div>
        ) : (
          <div className="grid lg:grid-cols-3 gap-8">
            {/* Cart Items */}
            <div className="lg:col-span-2 space-y-4">
              {cartItems.map((item: any) => (
                <div key={item.id} className="bg-white p-6 rounded-xl shadow-md" data-testid={`cart-item-${item.id}`}>
                  <div className="flex items-center space-x-4">
                    <img 
                      src={item.product?.imageUrl || "https://images.unsplash.com/photo-1519238263530-99bdd11df2ea?ixlib=rb-4.0.3&auto=format&fit=crop&w=100&h=100"}
                      alt={item.product?.name}
                      className="w-20 h-20 object-cover rounded-lg"
                      data-testid={`img-cart-item-${item.id}`}
                    />
                    <div className="flex-1">
                      <h3 className="font-semibold text-lg" data-testid={`text-item-name-${item.id}`}>
                        {item.product?.name}
                      </h3>
                      <div className="text-sm text-gray-600 space-y-1">
                        {item.size && <p data-testid={`text-item-size-${item.id}`}>Size: {item.size}</p>}
                        {item.color && <p data-testid={`text-item-color-${item.id}`}>Color: {item.color}</p>}
                      </div>
                      <div className="flex items-center justify-between mt-3">
                        <div>
                          <span className="text-xl font-bold text-primary" data-testid={`text-item-price-${item.id}`}>
                            â‚¹{item.product?.salePrice || item.product?.price}
                          </span>
                          {item.product?.salePrice && (
                            <span className="text-gray-500 line-through ml-2" data-testid={`text-item-original-price-${item.id}`}>
                              â‚¹{item.product?.price}
                            </span>
                          )}
                        </div>
                        <div className="flex items-center space-x-3">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleUpdateQuantity(item.id, item.quantity - 1)}
                            disabled={updateCartMutation.isPending}
                            data-testid={`button-decrease-${item.id}`}
                          >
                            -
                          </Button>
                          <span className="font-medium w-8 text-center" data-testid={`text-item-quantity-${item.id}`}>
                            {item.quantity}
                          </span>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => handleUpdateQuantity(item.id, item.quantity + 1)}
                            disabled={updateCartMutation.isPending}
                            data-testid={`button-increase-${item.id}`}
                          >
                            +
                          </Button>
                          <Button
                            variant="destructive"
                            size="sm"
                            onClick={() => handleRemoveItem(item.id)}
                            disabled={removeFromCartMutation.isPending}
                            data-testid={`button-remove-${item.id}`}
                          >
                            Remove
                          </Button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>

            {/* Order Summary */}
            <div className="bg-white p-6 rounded-xl shadow-md h-fit">
              <h3 className="text-xl font-bold mb-6" data-testid="text-order-summary">Order Summary</h3>

              <div className="space-y-3 mb-6">
                <div className="flex justify-between">
                  <span data-testid="text-subtotal-label">Subtotal</span>
                  <span data-testid="text-subtotal-amount">â‚¹{subtotal.toFixed(2)}</span>
                </div>
                <div className="flex justify-between">
                  <span data-testid="text-shipping-label">Shipping</span>
                  <span data-testid="text-shipping-amount">
                    {isEligibleForFreeShipping ? "Free" : `â‚¹${shippingCost}`}
                  </span>
                </div>
                {!isEligibleForFreeShipping && (
                  <div className="text-sm text-primary">
                    <p data-testid="text-free-shipping-message">
                      Add â‚¹{(shippingThreshold - subtotal).toFixed(2)} more for free shipping!
                    </p>
                    <div className="w-full bg-gray-200 rounded-full h-2 mt-2">
                      <div 
                        className="bg-gradient-to-r from-primary to-secondary h-2 rounded-full" 
                        style={{ width: `${Math.min(100, (subtotal / shippingThreshold) * 100)}%` }}
                        data-testid="progress-free-shipping"
                      ></div>
                    </div>
                  </div>
                )}
                <div className="border-t pt-3">
                  <div className="flex justify-between text-xl font-bold">
                    <span data-testid="text-total-label">Total</span>
                    <span data-testid="text-total-amount">â‚¹{total.toFixed(2)}</span>
                  </div>
                </div>
              </div>

              <Button 
                onClick={handleCheckout}
                className="w-full bg-primary text-white py-4 rounded-full font-semibold text-lg hover:bg-primary/90"
                data-testid="button-checkout"
              >
                Proceed to Checkout
              </Button>

              <Button 
                variant="outline"
                onClick={() => setLocation("/products")}
                className="w-full mt-3"
                data-testid="button-continue-shopping-summary"
              >
                Continue Shopping
              </Button>
            </div>
          </div>
        )}
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/checkout.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { useLocation } from "wouter";
import Header from "@/components/header";
import Footer from "@/components/footer";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";

const checkoutSchema = z.object({
  email: z.string().email("Please enter a valid email"),
  firstName: z.string().min(1, "First name is required"),
  lastName: z.string().min(1, "Last name is required"),
  phone: z.string().min(10, "Please enter a valid phone number"),
  address: z.string().min(1, "Address is required"),
  city: z.string().min(1, "City is required"),
  state: z.string().min(1, "State is required"),
  pincode: z.string().min(6, "Please enter a valid pincode"),
  paymentMethod: z.string().min(1, "Please select a payment method"),
  notes: z.string().optional(),
});

type CheckoutForm = z.infer<typeof checkoutSchema>;

export default function Checkout() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [, setLocation] = useLocation();

  const { data: cartItems = [], isLoading } = useQuery({
    queryKey: ["/api/cart"],
    retry: false,
    enabled: !!user,
  });

  const {
    register,
    handleSubmit,
    formState: { errors },
    setValue,
    watch,
  } = useForm<CheckoutForm>({
    resolver: zodResolver(checkoutSchema),
    defaultValues: {
      email: user?.email || "",
      firstName: user?.firstName || "",
      lastName: user?.lastName || "",
    },
  });

  const createOrderMutation = useMutation({
    mutationFn: async (orderData: any) => {
      return await apiRequest("POST", "/api/orders", orderData);
    },
    onSuccess: (response) => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Order Placed Successfully!",
        description: "Your order has been confirmed. You will receive an email confirmation shortly.",
      });
      setLocation(`/profile?tab=orders`);
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Order Failed",
        description: "Failed to place order. Please try again.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: CheckoutForm) => {
    if (cartItems.length === 0) {
      toast({
        title: "Empty Cart",
        description: "Your cart is empty. Add items to proceed.",
        variant: "destructive",
      });
      return;
    }

    const orderItems = cartItems.map((item: any) => ({
      productId: item.productId,
      quantity: item.quantity,
      size: item.size,
      color: item.color,
      price: item.product?.salePrice || item.product?.price,
    }));

    const shippingAddress = {
      firstName: data.firstName,
      lastName: data.lastName,
      email: data.email,
      phone: data.phone,
      address: data.address,
      city: data.city,
      state: data.state,
      pincode: data.pincode,
    };

    createOrderMutation.mutate({
      totalAmount: total.toFixed(2),
      shippingAddress,
      billingAddress: shippingAddress,
      paymentMethod: data.paymentMethod,
      notes: data.notes,
      items: orderItems,
    });
  };

  if (!user) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8 text-center">
          <h1 className="text-2xl font-bold text-gray-800 mb-4">Please Login</h1>
          <p className="text-gray-600 mb-6">You need to login to checkout</p>
          <Button onClick={() => window.location.href = "/api/login"}>
            Login
          </Button>
        </div>
        <Footer />
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="animate-pulse">
            <div className="h-8 bg-gray-200 rounded w-48 mb-8"></div>
            <div className="grid lg:grid-cols-2 gap-8">
              <div className="space-y-4">
                {Array.from({ length: 6 }).map((_, i) => (
                  <div key={i} className="h-12 bg-gray-200 rounded"></div>
                ))}
              </div>
              <div className="h-64 bg-gray-200 rounded"></div>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  const subtotal = cartItems.reduce((total: number, item: any) => {
    return total + (parseFloat(item.product?.salePrice || item.product?.price || "0") * item.quantity);
  }, 0);

  const shippingCost = subtotal >= 1000 ? 0 : 100;
  const tax = subtotal * 0.18; // 18% GST
  const total = subtotal + shippingCost + tax;

  if (cartItems.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8 text-center">
          <h1 className="text-2xl font-bold text-gray-800 mb-4">Your cart is empty</h1>
          <p className="text-gray-600 mb-6">Add items to your cart to proceed with checkout</p>
          <Button onClick={() => setLocation("/products")}>
            Continue Shopping
          </Button>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold text-gray-800 mb-8" data-testid="text-checkout-title">Checkout</h1>

        <form onSubmit={handleSubmit(onSubmit)}>
          <div className="grid lg:grid-cols-2 gap-8">
            {/* Checkout Form */}
            <div className="space-y-6">
              {/* Contact Information */}
              <div className="bg-white p-6 rounded-xl shadow-md">
                <h2 className="text-xl font-bold mb-4" data-testid="text-contact-info-title">Contact Information</h2>

                <div className="space-y-4">
                  <div>
                    <Label htmlFor="email">Email</Label>
                    <Input
                      id="email"
                      type="email"
                      {...register("email")}
                      placeholder="your@email.com"
                      data-testid="input-email"
                    />
                    {errors.email && (
                      <p className="text-red-500 text-sm mt-1" data-testid="error-email">{errors.email.message}</p>
                    )}
                  </div>

                  <div className="grid md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="firstName">First Name</Label>
                      <Input
                        id="firstName"
                        {...register("firstName")}
                        placeholder="First name"
                        data-testid="input-first-name"
                      />
                      {errors.firstName && (
                        <p className="text-red-500 text-sm mt-1" data-testid="error-first-name">{errors.firstName.message}</p>
                      )}
                    </div>

                    <div>
                      <Label htmlFor="lastName">Last Name</Label>
                      <Input
                        id="lastName"
                        {...register("lastName")}
                        placeholder="Last name"
                        data-testid="input-last-name"
                      />
                      {errors.lastName && (
                        <p className="text-red-500 text-sm mt-1" data-testid="error-last-name">{errors.lastName.message}</p>
                      )}
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="phone">Phone Number</Label>
                    <Input
                      id="phone"
                      type="tel"
                      {...register("phone")}
                      placeholder="10-digit phone number"
                      data-testid="input-phone"
                    />
                    {errors.phone && (
                      <p className="text-red-500 text-sm mt-1" data-testid="error-phone">{errors.phone.message}</p>
                    )}
                  </div>
                </div>
              </div>

              {/* Shipping Address */}
              <div className="bg-white p-6 rounded-xl shadow-md">
                <h2 className="text-xl font-bold mb-4" data-testid="text-shipping-address-title">Shipping Address</h2>

                <div className="space-y-4">
                  <div>
                    <Label htmlFor="address">Address</Label>
                    <Textarea
                      id="address"
                      {...register("address")}
                      placeholder="Street address, apartment, suite, etc."
                      data-testid="textarea-address"
                    />
                    {errors.address && (
                      <p className="text-red-500 text-sm mt-1" data-testid="error-address">{errors.address.message}</p>
                    )}
                  </div>

                  <div className="grid md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="city">City</Label>
                      <Input
                        id="city"
                        {...register("city")}
                        placeholder="City"
                        data-testid="input-city"
                      />
                      {errors.city && (
                        <p className="text-red-500 text-sm mt-1" data-testid="error-city">{errors.city.message}</p>
                      )}
                    </div>

                    <div>
                      <Label htmlFor="state">State</Label>
                      <Input
                        id="state"
                        {...register("state")}
                        placeholder="State"
                        data-testid="input-state"
                      />
                      {errors.state && (
                        <p className="text-red-500 text-sm mt-1" data-testid="error-state">{errors.state.message}</p>
                      )}
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="pincode">Pincode</Label>
                    <Input
                      id="pincode"
                      {...register("pincode")}
                      placeholder="6-digit pincode"
                      data-testid="input-pincode"
                    />
                    {errors.pincode && (
                      <p className="text-red-500 text-sm mt-1" data-testid="error-pincode">{errors.pincode.message}</p>
                    )}
                  </div>
                </div>
              </div>

              {/* Payment Method */}
              <div className="bg-white p-6 rounded-xl shadow-md">
                <h2 className="text-xl font-bold mb-4" data-testid="text-payment-method-title">Payment Method</h2>

                <div className="space-y-4">
                  <Select onValueChange={(value) => setValue("paymentMethod", value)}>
                    <SelectTrigger data-testid="select-payment-method">
                      <SelectValue placeholder="Select payment method" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="cod">Cash on Delivery</SelectItem>
                      <SelectItem value="card">Credit/Debit Card</SelectItem>
                      <SelectItem value="upi">UPI</SelectItem>
                      <SelectItem value="netbanking">Net Banking</SelectItem>
                    </SelectContent>
                  </Select>
                  {errors.paymentMethod && (
                    <p className="text-red-500 text-sm mt-1" data-testid="error-payment-method">{errors.paymentMethod.message}</p>
                  )}
                </div>
              </div>

              {/* Order Notes */}
              <div className="bg-white p-6 rounded-xl shadow-md">
                <h2 className="text-xl font-bold mb-4" data-testid="text-order-notes-title">Order Notes (Optional)</h2>

                <Textarea
                  {...register("notes")}
                  placeholder="Any special instructions for your order..."
                  data-testid="textarea-order-notes"
                />
              </div>
            </div>

            {/* Order Summary */}
            <div>
              <div className="bg-white p-6 rounded-xl shadow-md sticky top-24">
                <h2 className="text-xl font-bold mb-6" data-testid="text-order-summary-title">Order Summary</h2>

                {/* Order Items */}
                <div className="space-y-4 mb-6">
                  {cartItems.map((item: any) => (
                    <div key={item.id} className="flex items-center space-x-4" data-testid={`order-item-${item.id}`}>
                      <img
                        src={item.product?.imageUrl || "https://images.unsplash.com/photo-1519238263530-99bdd11df2ea?ixlib=rb-4.0.3&auto=format&fit=crop&w=80&h=80"}
                        alt={item.product?.name}
                        className="w-16 h-16 object-cover rounded-lg"
                        data-testid={`img-order-item-${item.id}`}
                      />
                      <div className="flex-1">
                        <h3 className="font-medium" data-testid={`text-order-item-name-${item.id}`}>
                          {item.product?.name}
                        </h3>
                        <div className="text-sm text-gray-600">
                          {item.size && <span data-testid={`text-order-item-size-${item.id}`}>Size: {item.size}</span>}
                          {item.color && <span className="ml-2" data-testid={`text-order-item-color-${item.id}`}>Color: {item.color}</span>}
                        </div>
                        <div className="flex justify-between items-center">
                          <span className="text-sm text-gray-500" data-testid={`text-order-item-quantity-${item.id}`}>
                            Qty: {item.quantity}
                          </span>
                          <span className="font-semibold" data-testid={`text-order-item-total-${item.id}`}>
                            â‚¹{(parseFloat(item.product?.salePrice || item.product?.price || "0") * item.quantity).toFixed(2)}
                          </span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>

                {/* Price Breakdown */}
                <div className="space-y-3 border-t pt-4">
                  <div className="flex justify-between">
                    <span data-testid="text-order-subtotal-label">Subtotal</span>
                    <span data-testid="text-order-subtotal-amount">â‚¹{subtotal.toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between">
                    <span data-testid="text-order-shipping-label">Shipping</span>
                    <span data-testid="text-order-shipping-amount">
                      {shippingCost === 0 ? "Free" : `â‚¹${shippingCost.toFixed(2)}`}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span data-testid="text-order-tax-label">Tax (18% GST)</span>
                    <span data-testid="text-order-tax-amount">â‚¹{tax.toFixed(2)}</span>
                  </div>
                  <div className="border-t pt-3">
                    <div className="flex justify-between text-xl font-bold">
                      <span data-testid="text-order-total-label">Total</span>
                      <span data-testid="text-order-total-amount">â‚¹{total.toFixed(2)}</span>
                    </div>
                  </div>
                </div>

                {/* Place Order Button */}
                <Button
                  type="submit"
                  disabled={createOrderMutation.isPending}
                  className="w-full mt-6 bg-primary text-white py-4 rounded-full font-semibold text-lg hover:bg-primary/90"
                  data-testid="button-place-order"
                >
                  {createOrderMutation.isPending ? "Placing Order..." : "Place Order"}
                </Button>

                <p className="text-xs text-gray-500 text-center mt-4" data-testid="text-order-terms">
                  By placing your order, you agree to our terms and conditions and privacy policy.
                </p>
              </div>
            </div>
          </div>
        </form>
      </div>

      <Footer />
    </div>
  );
}

client/src/pages/home.tsx.content:
import { useEffect, useState } from "react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import Header from "@/components/header";
import Footer from "@/components/footer";
import ProductCard from "@/components/product-card";
import CartSidebar from "@/components/cart-sidebar";
import { Button } from "@/components/ui/button";
import { useQuery } from "@tanstack/react-query";
import type { Product, Category, Slider, SiteSetting } from "@shared/schema";

export default function Home() {
  const { toast } = useToast();
  const { isAuthenticated, isLoading } = useAuth();
  const [currentSlide, setCurrentSlide] = useState(0);
  const totalSlides = sliders.length || 1;

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  const { data: featuredProducts = [], isLoading: featuredLoading } = useQuery<Product[]>({
    queryKey: ["/api/products/featured"],
    retry: false,
  });

  const { data: saleProducts = [], isLoading: saleLoading } = useQuery<Product[]>({
    queryKey: ["/api/products/sale"],
    retry: false,
  });

  const { data: categories = [], isLoading: categoriesLoading } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
    retry: false,
  });

  const { data: sliders = [], isLoading: slidersLoading } = useQuery<Slider[]>({
    queryKey: ["/api/sliders", { placement: "home" }],
    retry: false,
  });

  const { data: siteSettings = [], isLoading: settingsLoading } = useQuery<SiteSetting[]>({
    queryKey: ["/api/site-settings", { category: "home" }],
    retry: false,
  });

  const nextSlide = () => {
    if (totalSlides > 1) {
      setCurrentSlide((prev) => (prev + 1) % totalSlides);
    }
  };

  const prevSlide = () => {
    if (totalSlides > 1) {
      setCurrentSlide((prev) => (prev - 1 + totalSlides) % totalSlides);
    }
  };

  const goToSlide = (index: number) => {
    setCurrentSlide(index);
  };

  // Auto-play slider
  useEffect(() => {
    if (totalSlides > 1) {
      const interval = setInterval(nextSlide, 4000);
      return () => clearInterval(interval);
    }
  }, [totalSlides]);


  if (isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white">
      <Header />

      {/* Dynamic Sliders */}
      {sliders.length > 0 && (
        <section className="relative w-full h-[400px] md:h-[500px] overflow-hidden">
          <div className="slider-container relative w-full h-full">
            {sliders.map((slider, index) => (
              <div 
                key={slider.id}
                className={`slide absolute inset-0 w-full h-full transition-transform duration-500 ${
                  index === currentSlide ? 'translate-x-0' : 
                  index < currentSlide ? '-translate-x-full' : 'translate-x-full'
                }`}
                style={{
                  backgroundImage: `linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.4)), url(${slider.imageUrl})`,
                  backgroundSize: 'cover',
                  backgroundPosition: 'center'
                }}
              >
                <div className="absolute inset-0 flex flex-col items-center justify-center text-white text-center p-6">
                  <h1 className="text-4xl md:text-6xl font-black mb-4 tracking-wide">{slider.title}</h1>
                  {slider.description && (
                    <p className="text-lg md:text-xl font-medium mb-6 max-w-2xl">{slider.description}</p>
                  )}
                  {slider.linkUrl && slider.buttonText && (
                    <a 
                      href={slider.linkUrl}
                      className="bg-white text-black px-8 py-3 rounded-lg font-semibold text-lg hover:bg-gray-100 transition-colors"
                      data-testid={`slider-button-${slider.id}`}
                    >
                      {slider.buttonText}
                    </a>
                  )}
                </div>
              </div>
            ))}

            {/* Slider Controls */}
            {sliders.length > 1 && (
              <>
                <button 
                  onClick={prevSlide} 
                  className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-70 transition-all"
                  data-testid="slider-prev"
                >
                  â†
                </button>
                <button 
                  onClick={nextSlide} 
                  className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-black bg-opacity-50 text-white p-2 rounded-full hover:bg-opacity-70 transition-all"
                  data-testid="slider-next"
                >
                  â†’
                </button>

                {/* Slider Dots */}
                <div className="absolute bottom-6 left-1/2 transform -translate-x-1/2 flex space-x-2">
                  {sliders.map((_, index) => (
                    <button
                      key={index}
                      onClick={() => goToSlide(index)}
                      className={`w-3 h-3 rounded-full transition-all ${
                        index === currentSlide ? 'bg-white' : 'bg-white bg-opacity-50'
                      }`}
                      data-testid={`slider-dot-${index}`}
                    />
                  ))}
                </div>
              </>
            )}
          </div>
        </section>
      )}


      {/* Promotional Banner */}
      <section className="py-4 px-4 bg-white">
        <div className="container mx-auto">
          <div className="bg-gradient-to-r from-yellow-400 to-orange-400 rounded-xl p-4">
