import { useState } from "react";
import { useParams } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import Header from "@/components/header";
import Footer from "@/components/footer";
import CartSidebar from "@/components/cart-sidebar";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

export default function ProductDetail() {
  const { slug } = useParams();
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();

  const [selectedSize, setSelectedSize] = useState("");
  const [selectedColor, setSelectedColor] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [activeImage, setActiveImage] = useState(0);
  const [showReviewForm, setShowReviewForm] = useState(false);
  const [reviewData, setReviewData] = useState({ rating: 5, title: "", comment: "" });

  const { data: product, isLoading } = useQuery({
    queryKey: ["/api/products", slug],
    retry: false,
  });

  const { data: reviews = [] } = useQuery({
    queryKey: ["/api/products", product?.id, "reviews"],
    enabled: !!product?.id,
    retry: false,
  });

  const addToCartMutation = useMutation({
    mutationFn: async (cartData: any) => {
      await apiRequest("POST", "/api/cart", cartData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/cart"] });
      toast({
        title: "Added to Cart",
        description: "Product has been added to your cart!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add product to cart",
        variant: "destructive",
      });
    },
  });

  const addToWishlistMutation = useMutation({
    mutationFn: async (productId: string) => {
      await apiRequest("POST", "/api/wishlist", { productId });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/wishlist"] });
      toast({
        title: "Added to Wishlist",
        description: "Product has been added to your wishlist!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to add product to wishlist",
        variant: "destructive",
      });
    },
  });

  const createReviewMutation = useMutation({
    mutationFn: async (reviewData: any) => {
      await apiRequest("POST", `/api/products/${product?.id}/reviews`, reviewData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/products", product?.id, "reviews"] });
      setShowReviewForm(false);
      setReviewData({ rating: 5, title: "", comment: "" });
      toast({
        title: "Review Submitted",
        description: "Thank you for your review!",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error",
        description: "Failed to submit review",
        variant: "destructive",
      });
    },
  });

  const handleAddToCart = () => {
    if (!user) {
      toast({
        title: "Please Login",
        description: "You need to login to add items to cart",
        variant: "destructive",
      });
      return;
    }

    if (product?.sizes && product.sizes.length > 0 && !selectedSize) {
      toast({
        title: "Select Size",
        description: "Please select a size",
        variant: "destructive",
      });
      return;
    }

    addToCartMutation.mutate({
      productId: product?.id,
      quantity,
      size: selectedSize || null,
      color: selectedColor || null,
    });
  };

  const handleAddToWishlist = () => {
    if (!user) {
      toast({
        title: "Please Login",
        description: "You need to login to add items to wishlist",
        variant: "destructive",
      });
      return;
    }

    addToWishlistMutation.mutate(product?.id);
  };

  const handleSubmitReview = () => {
    if (!user) {
      toast({
        title: "Please Login",
        description: "You need to login to submit a review",
        variant: "destructive",
      });
      return;
    }

    createReviewMutation.mutate(reviewData);
  };

  const displayPrice = product?.salePrice || product?.price;
  const originalPrice = product?.salePrice ? product?.price : null;
  const discountPercent = originalPrice 
    ? Math.round(((parseFloat(originalPrice) - parseFloat(displayPrice)) / parseFloat(originalPrice)) * 100)
    : 0;

  const images = product?.images && product.images.length > 0 
    ? product.images 
    : [product?.imageUrl || "https://images.unsplash.com/photo-1519238263530-99bdd11df2ea?ixlib=rb-4.0.3&auto=format&fit=crop&w=600&h=600"];

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="grid md:grid-cols-2 gap-8 animate-pulse">
            <div className="space-y-4">
              <div className="w-full h-96 bg-gray-200 rounded-xl"></div>
              <div className="grid grid-cols-4 gap-2">
                {Array.from({ length: 4 }).map((_, i) => (
                  <div key={i} className="w-full h-20 bg-gray-200 rounded-lg"></div>
                ))}
              </div>
            </div>
            <div className="space-y-4">
              <div className="h-8 bg-gray-200 rounded"></div>
              <div className="h-6 bg-gray-200 rounded w-32"></div>
              <div className="h-10 bg-gray-200 rounded w-40"></div>
              <div className="h-24 bg-gray-200 rounded"></div>
            </div>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  if (!product) {
    return (
      <div className="min-h-screen bg-gray-50">
        <Header />
        <div className="container mx-auto px-4 py-8 text-center">
          <h1 className="text-2xl font-bold text-gray-800 mb-4">Product Not Found</h1>
          <p className="text-gray-600">The product you're looking for doesn't exist.</p>
        </div>
        <Footer />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="grid md:grid-cols-2 gap-8 mb-12">
          {/* Product Images */}
          <div>
            <div className="mb-4">
              <img 
                src={images[activeImage]} 
                alt={product.name}
                className="w-full h-96 object-cover rounded-xl"
                data-testid="img-product-main"
              />
            </div>
            <div className="grid grid-cols-4 gap-2">
              {images.map((image: string, index: number) => (
                <img
                  key={index}
                  src={image}
                  alt={`${product.name} ${index + 1}`}
                  className={`w-full h-20 object-cover rounded-lg cursor-pointer border-2 ${
                    activeImage === index ? "border-primary" : "border-gray-200"
                  }`}
                  onClick={() => setActiveImage(index)}
                  data-testid={`img-thumbnail-${index}`}
                />
              ))}
            </div>
          </div>

          {/* Product Details */}
          <div>
            <h1 className="text-3xl font-bold text-gray-800 mb-4" data-testid="text-product-name">{product.name}</h1>

            <div className="flex items-center mb-4">
              <div className="star-rating text-warning mr-2">
                {Array.from({ length: 5 }, (_, i) => (
                  <i key={i} className={`${i < Math.floor(parseFloat(product.rating || "0")) ? "fas" : "far"} fa-star`}></i>
                ))}
              </div>
              <span className="text-gray-500 text-sm" data-testid="text-review-count">({product.reviewCount} reviews)</span>
            </div>

            <div className="mb-6">
              <div className="flex items-center space-x-2">
                <span className="text-3xl font-bold text-primary" data-testid="text-price">â‚¹{displayPrice}</span>
                {originalPrice && (
                  <>
                    <span className="text-gray-500 line-through text-xl" data-testid="text-original-price">â‚¹{originalPrice}</span>
                    <Badge variant="destructive" data-testid="badge-discount">{discountPercent}% OFF</Badge>
                  </>
                )}
              </div>
            </div>

            {product.shortDescription && (
              <p className="text-gray-600 mb-6" data-testid="text-product-description">{product.shortDescription}</p>
            )}

            {/* Size Selection */}
            {product.sizes && product.sizes.length > 0 && (
              <div className="mb-6">
                <h4 className="font-semibold mb-2">Select Size/Age:</h4>
                <Select value={selectedSize} onValueChange={setSelectedSize}>
                  <SelectTrigger className="w-full" data-testid="select-size">
                    <SelectValue placeholder="Choose size" />
                  </SelectTrigger>
                  <SelectContent>
                    {product.sizes.map((size: string) => (
                      <SelectItem key={size} value={size}>{size}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            )}

            {/* Color Selection */}
            {product.colors && product.colors.length > 0 && (
              <div className="mb-6">
                <h4 className="font-semibold mb-2">Select Color:</h4>
                <Select value={selectedColor} onValueChange={setSelectedColor}>
                  <SelectTrigger className="w-full" data-testid="select-color">
                    <SelectValue placeholder="Choose color" />
                  </SelectTrigger>
                  <SelectContent>
                    {product.colors.map((color: string) => (
                      <SelectItem key={color} value={color}>{color}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            )}

            {/* Quantity */}
            <div className="mb-6">
              <h4 className="font-semibold mb-2">Quantity:</h4>
              <div className="flex items-center space-x-3">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setQuantity(Math.max(1, quantity - 1))}
                  data-testid="button-decrease-quantity"
                >
                  -
                </Button>
                <span className="text-lg font-medium" data-testid="text-quantity">{quantity}</span>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => setQuantity(quantity + 1)}
                  data-testid="button-increase-quantity"
                >
                  +
                </Button>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="space-y-3 mb-6">
              <Button
                onClick={handleAddToCart}
                disabled={addToCartMutation.isPending}
                className="w-full bg-primary text-white py-4 rounded-full font-semibold text-lg hover:bg-primary/90"
                data-testid="button-add-to-cart"
              >
                {addToCartMutation.isPending ? "Adding..." : "Add to Cart"}
              </Button>
              <Button
                variant="outline"
                onClick={handleAddToWishlist}
                disabled={addToWishlistMutation.isPending}
                className="w-full py-3 rounded-full font-medium"
                data-testid="button-add-to-wishlist"
              >
                {addToWishlistMutation.isPending ? "Adding..." : "Add to Wishlist"}
              </Button>
            </div>

            {/* Product Features */}
            <div className="text-sm text-gray-600 space-y-1">
              <p data-testid="text-shipping-info"><i className="fas fa-truck mr-2"></i>Free shipping on orders above â‚¹1000</p>
              <p data-testid="text-return-policy"><i className="fas fa-undo mr-2"></i>30-day return policy</p>
              <p data-testid="text-quality-guarantee"><i className="fas fa-shield-alt mr-2"></i>Quality guaranteed</p>
            </div>
          </div>
        </div>

        {/* Product Description */}
        {product.description && (
          <div className="bg-white p-6 rounded-xl shadow-md mb-8">
            <h3 className="text-xl font-bold mb-4" data-testid="text-description-title">Product Description</h3>
            <p className="text-gray-600 leading-relaxed" data-testid="text-full-description">{product.description}</p>
          </div>
        )}

        {/* Reviews Section */}
        <div className="bg-white p-6 rounded-xl shadow-md">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-xl font-bold" data-testid="text-reviews-title">Customer Reviews</h3>
            {user && (
              <Button 
                onClick={() => setShowReviewForm(!showReviewForm)}
                data-testid="button-write-review"
              >
                Write a Review
              </Button>
            )}
          </div>

          {/* Review Form */}
          {showReviewForm && (
            <div className="mb-6 p-4 border rounded-lg">
              <h4 className="font-semibold mb-4" data-testid="text-review-form-title">Write Your Review</h4>

              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Rating</label>
                <div className="flex space-x-1">
                  {[1, 2, 3, 4, 5].map((star) => (
                    <button
                      key={star}
                      onClick={() => setReviewData({ ...reviewData, rating: star })}
                      className={`text-2xl ${star <= reviewData.rating ? "text-warning" : "text-gray-300"}`}
                      data-testid={`button-rating-${star}`}
                    >
                      <i className="fas fa-star"></i>
                    </button>
                  ))}
                </div>
              </div>

              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Title</label>
                <input
                  type="text"
                  value={reviewData.title}
                  onChange={(e) => setReviewData({ ...reviewData, title: e.target.value })}
                  className="w-full px-3 py-2 border rounded-lg"
                  placeholder="Review title"
                  data-testid="input-review-title"
                />
              </div>

              <div className="mb-4">
                <label className="block text-sm font-medium mb-2">Comment</label>
                <Textarea
                  value={reviewData.comment}
                  onChange={(e) => setReviewData({ ...reviewData, comment: e.target.value })}
                  placeholder="Share your experience..."
                  data-testid="textarea-review-comment"
                />
              </div>

              <div className="flex space-x-2">
                <Button 
                  onClick={handleSubmitReview}
                  disabled={createReviewMutation.isPending}
                  data-testid="button-submit-review"
                >
                  {createReviewMutation.isPending ? "Submitting..." : "Submit Review"}
                </Button>
                <Button 
                  variant="outline" 
                  onClick={() => setShowReviewForm(false)}
                  data-testid="button-cancel-review"
                >
                  Cancel
                </Button>
              </div>
            </div>
          )}

          {/* Reviews List */}
          <div className="space-y-4">
            {reviews.length > 0 ? (
              reviews.map((review: any, index: number) => (
                <div key={review.id} className="border-b pb-4" data-testid={`review-${index}`}>
                  <div className="flex items-center justify-between mb-2">
                    <div className="flex items-center space-x-2">
                      <div className="star-rating text-warning">
                        {Array.from({ length: 5 }, (_, i) => (
                          <i key={i} className={`${i < review.rating ? "fas" : "far"} fa-star`}></i>
                        ))}
                      </div>
                      {review.title && <span className="font-medium" data-testid={`text-review-title-${index}`}>{review.title}</span>}
                    </div>
                    <span className="text-sm text-gray-500" data-testid={`text-review-date-${index}`}>
                      {new Date(review.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                  {review.comment && (
                    <p className="text-gray-600" data-testid={`text-review-comment-${index}`}>{review.comment}</p>
                  )}
                </div>
              ))
            ) : (
              <p className="text-gray-500 text-center py-8" data-testid="text-no-reviews">No reviews yet. Be the first to review this product!</p>
            )}
          </div>
        </div>
      </div>

      <Footer />
      <CartSidebar />
    </div>
  );
}

client/src/pages/products.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import type { Product, Category } from "@shared/schema";
import Header from "@/components/header";
import Footer from "@/components/footer";
import ProductCard from "@/components/product-card";
import CartSidebar from "@/components/cart-sidebar";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Slider } from "@/components/ui/slider";
import { Label } from "@/components/ui/label";

export default function Products() {
  const [filters, setFilters] = useState({
    search: "",
    categoryId: "",
    minPrice: 0,
    maxPrice: 5000,
    isOnSale: false,
    page: 1,
  });

  const { data: categories = [] } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
    retry: false,
  });

  const { data: productsData, isLoading } = useQuery<{
    products: Product[];
    total: number;
    page: number;
    limit: number;
    totalPages: number;
  }>({
    queryKey: ["/api/products", filters],
    retry: false,
  });

  const handleFilterChange = (key: string, value: any) => {
    setFilters(prev => ({ ...prev, [key]: value, page: 1 }));
  };

  const handlePriceChange = (value: number[]) => {
    setFilters(prev => ({ ...prev, minPrice: value[0], maxPrice: value[1] }));
  };

  const clearFilters = () => {
    setFilters({
      search: "",
      categoryId: "",
      minPrice: 0,
      maxPrice: 5000,
      isOnSale: false,
      page: 1,
    });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Filters Sidebar */}
          <div className="lg:w-1/4">
            <div className="bg-white p-6 rounded-xl shadow-md">
              <div className="flex items-center justify-between mb-6">
                <h3 className="text-lg font-semibold" data-testid="text-filters-title">Filters</h3>
                <Button 
                  variant="ghost" 
                  onClick={clearFilters}
                  className="text-sm text-primary"
                  data-testid="button-clear-filters"
                >
                  Clear All
                </Button>
              </div>

              {/* Search */}
              <div className="mb-6">
                <Label htmlFor="search" className="text-sm font-medium mb-2 block">Search</Label>
                <Input
                  id="search"
                  placeholder="Search products..."
                  value={filters.search}
                  onChange={(e) => handleFilterChange("search", e.target.value)}
                  data-testid="input-search"
                />
              </div>

              {/* Category */}
              <div className="mb-6">
                <Label className="text-sm font-medium mb-2 block">Category</Label>
                <Select value={filters.categoryId} onValueChange={(value) => handleFilterChange("categoryId", value)}>
                  <SelectTrigger data-testid="select-category">
                    <SelectValue placeholder="All Categories" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="">All Categories</SelectItem>
                    {categories.map((category: any) => (
                      <SelectItem key={category.id} value={category.id}>
                        {category.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {/* Price Range */}
              <div className="mb-6">
                <Label className="text-sm font-medium mb-2 block">Price Range</Label>
                <div className="px-2">
                  <Slider
                    value={[filters.minPrice, filters.maxPrice]}
                    onValueChange={handlePriceChange}
                    min={0}
                    max={5000}
                    step={100}
                    className="mb-4"
                    data-testid="slider-price"
                  />
                  <div className="flex justify-between text-sm text-gray-600">
                    <span data-testid="text-min-price">â‚¹{filters.minPrice}</span>
                    <span data-testid="text-max-price">â‚¹{filters.maxPrice}</span>
                  </div>
                </div>
              </div>

              {/* Sale Only */}
              <div className="mb-6">
                <label className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={filters.isOnSale}
                    onChange={(e) => handleFilterChange("isOnSale", e.target.checked)}
                    className="rounded border-gray-300"
                    data-testid="checkbox-sale-only"
                  />
                  <span className="text-sm font-medium">Sale Items Only</span>
                </label>
              </div>
            </div>
          </div>

          {/* Products Grid */}
          <div className="lg:w-3/4">
            <div className="mb-6">
              <div className="flex items-center justify-between">
                <h1 className="text-2xl font-bold" data-testid="text-products-title">Products</h1>
                {productsData && (
                  <p className="text-gray-600" data-testid="text-products-count">
                    {productsData.total} products found
                  </p>
                )}
              </div>
            </div>

            {isLoading ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {Array.from({ length: 6 }).map((_, i) => (
                  <div key={i} className="bg-white rounded-xl shadow-md overflow-hidden animate-pulse">
                    <div className="w-full h-64 bg-gray-200"></div>
                    <div className="p-4">
                      <div className="h-4 bg-gray-200 rounded mb-2"></div>
                      <div className="h-6 bg-gray-200 rounded mb-2"></div>
                      <div className="h-8 bg-gray-200 rounded"></div>
                    </div>
                  </div>
                ))}
              </div>
            ) : productsData?.products?.length > 0 ? (
              <>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
                  {productsData.products.map((product: any) => (
                    <ProductCard key={product.id} product={product} />
                  ))}
                </div>

                {/* Pagination */}
                {productsData.totalPages > 1 && (
                  <div className="flex justify-center items-center space-x-2">
                    <Button
                      variant="outline"
                      onClick={() => handleFilterChange("page", Math.max(1, filters.page - 1))}
                      disabled={filters.page === 1}
                      data-testid="button-prev-page"
                    >
                      Previous
                    </Button>

                    <div className="flex space-x-1">
                      {Array.from({ length: Math.min(5, productsData.totalPages) }, (_, i) => {
                        const page = i + 1;
                        return (
                          <Button
                            key={page}
                            variant={filters.page === page ? "default" : "outline"}
                            onClick={() => handleFilterChange("page", page)}
                            className="w-10 h-10"
                            data-testid={`button-page-${page}`}
                          >
                            {page}
                          </Button>
                        );
                      })}
                    </div>

                    <Button
                      variant="outline"
                      onClick={() => handleFilterChange("page", Math.min(productsData.totalPages, filters.page + 1))}
                      disabled={filters.page === productsData.totalPages}
                      data-testid="button-next-page"
                    >
                      Next
                    </Button>
                  </div>
                )}
              </>
            ) : (
              <div className="text-center py-12">
                <div className="text-gray-500 text-lg mb-4" data-testid="text-no-products">No products found</div>
                <Button onClick={clearFilters} data-testid="button-clear-filters-empty">
                  Clear Filters
                </Button>
              </div>
            )}
          </div>
        </div>
      </div>

      <Footer />
      <CartSidebar />
    </div>
  );
}

client/src/pages/profile.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import Header from "@/components/header";
import Footer from "@/components/footer";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";

export default function Profile() {
  const { user, isLoading: authLoading } = useAuth();
  const { toast } = useToast();
  const [activeTab, setActiveTab] = useState("profile");

  // Redirect to home if not authenticated
  useEffect(() => {
    if (!authLoading && !user) {
      toast({
        title: "Unauthorized",
        description: "You are logged out. Logging in again...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [user, authLoading, toast]);

  // Check URL params for tab
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const tab = params.get("tab");
    if (tab) {
      setActiveTab(tab);
    }
  }, []);

  const { data: orders = [], isLoading: ordersLoading } = useQuery({
    queryKey: ["/api/orders"],
    retry: false,
    enabled: !!user,
  });

  const { data: wishlistItems = [], isLoading: wishlistLoading } = useQuery({
    queryKey: ["/api/wishlist"],
    retry: false,
    enabled: !!user,
  });

  if (authLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  if (!user) {
    return null; // Redirect is handled in useEffect
  }

  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case "pending":
        return "bg-yellow-100 text-yellow-800";
      case "processing":
        return "bg-blue-100 text-blue-800";
      case "shipped":
        return "bg-purple-100 text-purple-800";
      case "delivered":
        return "bg-green-100 text-green-800";
      case "cancelled":
        return "bg-red-100 text-red-800";
      default:
        return "bg-gray-100 text-gray-800";
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="max-w-4xl mx-auto">
          <h1 className="text-3xl font-bold text-gray-800 mb-8" data-testid="text-profile-title">My Account</h1>

          <Tabs value={activeTab} onValueChange={setActiveTab}>
            <TabsList className="grid w-full grid-cols-3" data-testid="tabs-profile">
              <TabsTrigger value="profile" data-testid="tab-profile">Profile</TabsTrigger>
              <TabsTrigger value="orders" data-testid="tab-orders">Orders</TabsTrigger>
              <TabsTrigger value="wishlist" data-testid="tab-wishlist">Wishlist</TabsTrigger>
            </TabsList>

            {/* Profile Tab */}
            <TabsContent value="profile" className="space-y-6">
              <div className="bg-white p-6 rounded-xl shadow-md">
                <h2 className="text-xl font-bold mb-4" data-testid="text-profile-info-title">Profile Information</h2>

                <div className="flex items-center space-x-4 mb-6">
                  {user.profileImageUrl ? (
                    <img
                      src={user.profileImageUrl}
                      alt="Profile"
                      className="w-16 h-16 rounded-full object-cover"
                      data-testid="img-profile-picture"
                    />
                  ) : (
                    <div className="w-16 h-16 bg-primary rounded-full flex items-center justify-center text-white font-bold text-2xl">
                      {user.firstName?.[0] || user.email?.[0] || "U"}
                    </div>
                  )}
                  <div>
                    <h3 className="text-lg font-semibold" data-testid="text-user-name">
                      {user.firstName} {user.lastName}
                    </h3>
                    <p className="text-gray-600" data-testid="text-user-email">{user.email}</p>
                  </div>
                </div>

                <div className="grid md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">First Name</label>
                    <div className="p-3 bg-gray-50 rounded-lg" data-testid="text-first-name">
                      {user.firstName || "Not provided"}
                    </div>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Last Name</label>
                    <div className="p-3 bg-gray-50 rounded-lg" data-testid="text-last-name">
                      {user.lastName || "Not provided"}
                    </div>
                  </div>
                  <div className="md:col-span-2">
                    <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                    <div className="p-3 bg-gray-50 rounded-lg" data-testid="text-email">
                      {user.email}
                    </div>
                  </div>
                </div>

                <div className="mt-6 pt-6 border-t">
                  <h3 className="font-semibold mb-2">Account Actions</h3>
                  <Button
                    onClick={() => window.location.href = "/api/logout"}
                    variant="outline"
                    data-testid="button-logout-profile"
                  >
                    Logout
                  </Button>
                </div>
              </div>
            </TabsContent>

            {/* Orders Tab */}
            <TabsContent value="orders" className="space-y-6">
              <div className="bg-white p-6 rounded-xl shadow-md">
                <h2 className="text-xl font-bold mb-4" data-testid="text-orders-title">Order History</h2>

                {ordersLoading ? (
                  <div className="space-y-4">
                    {Array.from({ length: 3 }).map((_, i) => (
                      <div key={i} className="animate-pulse">
                        <div className="h-24 bg-gray-200 rounded-lg"></div>
                      </div>
                    ))}
                  </div>
                ) : orders.length === 0 ? (
                  <div className="text-center py-8">
                    <i className="fas fa-shopping-bag text-4xl text-gray-300 mb-4"></i>
                    <p className="text-gray-500 mb-4" data-testid="text-no-orders">You haven't placed any orders yet</p>
                    <Button onClick={() => window.location.href = "/products"} data-testid="button-start-shopping">
                      Start Shopping
                    </Button>
                  </div>
                ) : (
                  <div className="space-y-4">
                    {orders.map((order: any) => (
                      <div key={order.id} className="border rounded-lg p-4" data-testid={`order-${order.id}`}>
                        <div className="flex items-center justify-between mb-2">
                          <div>
                            <h3 className="font-semibold" data-testid={`text-order-id-${order.id}`}>
                              Order #{order.id.slice(-8)}
                            </h3>
                            <p className="text-sm text-gray-600" data-testid={`text-order-date-${order.id}`}>
                              {new Date(order.createdAt).toLocaleDateString()}
                            </p>
                          </div>
                          <div className="text-right">
                            <Badge className={getStatusColor(order.status)} data-testid={`badge-order-status-${order.id}`}>
                              {order.status.toUpperCase()}
                            </Badge>
                            <p className="text-lg font-semibold mt-1" data-testid={`text-order-total-${order.id}`}>
                              â‚¹{order.totalAmount}
                            </p>
                          </div>
                        </div>

                        <div className="text-sm text-gray-600">
                          <p data-testid={`text-order-payment-${order.id}`}>
                            Payment: {order.paymentMethod || "Not specified"}
                          </p>
                          {order.shippingAddress && (
                            <p data-testid={`text-order-address-${order.id}`}>
                              Shipping to: {order.shippingAddress.city}, {order.shippingAddress.state}
                            </p>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </TabsContent>

            {/* Wishlist Tab */}
            <TabsContent value="wishlist" className="space-y-6">
              <div className="bg-white p-6 rounded-xl shadow-md">
                <h2 className="text-xl font-bold mb-4" data-testid="text-wishlist-title">My Wishlist</h2>

                {wishlistLoading ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {Array.from({ length: 6 }).map((_, i) => (
                      <div key={i} className="animate-pulse">
                        <div className="w-full h-48 bg-gray-200 rounded-lg mb-2"></div>
                        <div className="h-4 bg-gray-200 rounded mb-1"></div>
                        <div className="h-6 bg-gray-200 rounded w-20"></div>
                      </div>
                    ))}
                  </div>
                ) : wishlistItems.length === 0 ? (
                  <div className="text-center py-8">
                    <i className="fas fa-heart text-4xl text-gray-300 mb-4"></i>
                    <p className="text-gray-500 mb-4" data-testid="text-no-wishlist-items">Your wishlist is empty</p>
                    <Button onClick={() => window.location.href = "/products"} data-testid="button-browse-products">
                      Browse Products
                    </Button>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {wishlistItems.map((item: any) => (
                      <div key={item.id} className="border rounded-lg overflow-hidden" data-testid={`wishlist-item-${item.id}`}>
                        <img
                          src={item.product?.imageUrl || "https://images.unsplash.com/photo-1519238263530-99bdd11df2ea?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&h=300"}
                          alt={item.product?.name}
                          className="w-full h-48 object-cover"
                          data-testid={`img-wishlist-item-${item.id}`}
                        />
                        <div className="p-4">
                          <h3 className="font-semibold mb-2" data-testid={`text-wishlist-item-name-${item.id}`}>
                            {item.product?.name}
                          </h3>
                          <div className="flex items-center justify-between">
                            <span className="text-lg font-bold text-primary" data-testid={`text-wishlist-item-price-${item.id}`}>
                              â‚¹{item.product?.salePrice || item.product?.price}
                            </span>
                            <Button
                              size="sm"
                              onClick={() => window.location.href = `/products/${item.product?.slug}`}
                              data-testid={`button-view-wishlist-item-${item.id}`}
                            >
                              View
                            </Button>
